import MapWindow from '~/MapWindow';
import Task from '~/taskmanager/Task';
import {GwtkComponentDescriptionPropsData} from '~/types/Types';
import GwtkBackgroundLayersWidget from './GwtkBackgroundLayersWidget.vue';
import {ContentTreeNode} from '~/utils/MapTreeJSON';
import {
    LayerData,
    PROJECT_SETTINGS_LAYERS_BACKGROUND_ACTIVE, PROJECT_SETTINGS_LAYERS_OPACITY,
    PROJECT_SETTINGS_USER_INTERFACE_DARK_THEME_FLAG, WorkspaceValues
} from '~/utils/WorkspaceManager';
import Layer from '~/maplayers/Layer';

export const SET_ACTIVE_LAYER = 'gwtkbackgroundlayers.setactivelayer';
export const SET_ACTIVE_LAYER_OPACITY = 'gwtkbackgroundlayers.setactivelayeropacity';

export type GwtkBackgroundLayersTaskState = {
    [SET_ACTIVE_LAYER]: BackgroundLayer;
    [SET_ACTIVE_LAYER_OPACITY]: number;
}

export type BackgroundLayer = {
    id: string,
    alias: string,
    image: string,
    active: boolean
}

type WidgetParams = {
    setState: GwtkBackgroundLayersTask['setState'],
    backgroundLayers: BackgroundLayer[],
    imageByDefault: string,
    opacity: number,
}

export default class GwtkBackgroundLayersTask extends Task {

    private readonly imageLight = 'data:image/png;base64,';
    private readonly imageDark = 'data:image/png;base64,';

    private readonly widgetProps: GwtkComponentDescriptionPropsData & WidgetParams;

    constructor(mapWindow: MapWindow, id: string) {
        super(mapWindow, id);

        this.widgetProps = {
            taskId: this.id,
            description: this.mapWindow.getTaskManager().getTaskDescription(this.id),
            setState: this.setState.bind(this),
            backgroundLayers: [],
            imageByDefault: this.imageLight,
            opacity: 100
        };
    }

    createTaskPanel() {
        //  Vue 
        const nameWidget = 'GwtkBackgroundLayersWidget';
        const sourceWidget = GwtkBackgroundLayersWidget;
        this.mapWindow.registerComponent(nameWidget, sourceWidget);

        //  Vue 
        this.mapWindow.createWindowWidget(nameWidget, this.widgetProps);

        //      
        this.addToPostDeactivationList(this.widgetProps);
    }

    setup() {
        super.setup();

        this.setImageByDefault();

        const backgroundLayers: ContentTreeNode[] = this.map.contentTreeManager.getMapBackgrounds();

        if (backgroundLayers) {
            backgroundLayers.forEach(item => {
                this.widgetProps.backgroundLayers.push({
                    id: item.id,
                    alias: item.text,
                    image: GwtkBackgroundLayersTask.getImage(item),
                    active: false
                });
            });

        }

        if (this.widgetProps.backgroundLayers.length) {

            this.hideTileLayers();

            this.setActiveLayerInit();

        }

    }

    private setActiveLayerInit() {
        let id = '';

        const activeBackground: LayerData = this.map.workspaceManager.getValue(PROJECT_SETTINGS_LAYERS_BACKGROUND_ACTIVE) as unknown as LayerData;

        if (activeBackground) {
            const layer = this.widgetProps.backgroundLayers.find(item => item.id === activeBackground.id);
            if (layer) {
                id = layer.id;
            }
        } else {

            const backgroundLayers: ContentTreeNode[] = this.map.contentTreeManager.getMapBackgrounds();
            if (backgroundLayers) {
                backgroundLayers.forEach(item => {
                    if (item.backgroundactive) {
                        id = item.id;
                    }
                });
            }
        }

        const layer = this.widgetProps.backgroundLayers.find(item => item.id === id);
        if (layer) {
            layer.active = true;
        }

        const mapLayer = this.map.tiles.getLayerByxId(id);
        if (mapLayer) {
            this.map.setLayerVisibility(mapLayer, true);

            const layersOpacity: { id: string; opacity: number; }[] = this.map.workspaceManager.getValue(PROJECT_SETTINGS_LAYERS_OPACITY) as unknown as { id: string; opacity: number; }[];
            const activeBackgroundOpacity = layersOpacity.find(item => item.id === id)?.opacity;
            if (activeBackgroundOpacity !== undefined) {
                this.widgetProps.opacity = activeBackgroundOpacity * 100;
            }
        }

    }

    private hideTileLayers() {
        this.widgetProps.backgroundLayers.forEach(item => {
            const mapLayer = this.map.tiles.getLayerByxId(item.id);
            if (mapLayer) {
                this.map.setLayerVisibility(mapLayer, false);
            }
        });
    }

    private static getImage(contentTreeNode: ContentTreeNode): string {
        if (contentTreeNode.backgroundimage) {
            return 'data:image/png;base64,' + contentTreeNode.backgroundimage;
        }
        return '';
    }

    private setImageByDefault() {
        const isDarkTheme = this.map.workspaceManager.getValue(PROJECT_SETTINGS_USER_INTERFACE_DARK_THEME_FLAG);

        if (isDarkTheme) {
            this.widgetProps.imageByDefault = this.imageDark;
        } else {
            this.widgetProps.imageByDefault = this.imageLight;
        }
    }

    async clearTaskWorkspaceData(): Promise<void> {
        this.map.restoreBackgroundLayers();

        super.clearTaskWorkspaceData();
    }

    onWorkspaceChanged(type: keyof WorkspaceValues) {
        super.onWorkspaceChanged(type);
        if (type === PROJECT_SETTINGS_USER_INTERFACE_DARK_THEME_FLAG) {
            this.setImageByDefault();
        }
    }

    onWorkspaceReset() {
        super.onWorkspaceReset();
        this.setImageByDefault();
    }

    setState<K extends keyof GwtkBackgroundLayersTaskState>(key: K, value: GwtkBackgroundLayersTaskState[K]) {
        switch (key) {
            case SET_ACTIVE_LAYER:

                this.widgetProps.backgroundLayers.forEach(item => item.active = false);

                this.hideTileLayers();

                const backgroundLayer = value as BackgroundLayer;
                backgroundLayer.active = true;

                const mapLayer = this.map.tiles.getLayerByxId(backgroundLayer.id);
                if (mapLayer) {
                    this.map.setLayerVisibility(mapLayer, true);

                    this.widgetProps.opacity = mapLayer.options.opacity;

                    this.map.workspaceManager.setValue(PROJECT_SETTINGS_LAYERS_BACKGROUND_ACTIVE, mapLayer.id);
                }
                break;

            case SET_ACTIVE_LAYER_OPACITY:

                this.widgetProps.backgroundLayers.forEach(item => {
                    const layer: Layer | undefined = this.map.tiles.getLayerByxId(item.id);
                    if (layer && item.active) {
                        this.widgetProps.opacity = value as number;
                        this.map.handlers.changeLayerOpacity(layer, this.widgetProps.opacity);
                    }
                });

                break;
        }
    }

}
